import React, {useState} from 'react';
import Todo from './components/Todo';
import Header from './components/Header';
import Auth from './components/Auth';
import AuthcContext from './auth-context'

const app = props => {
  const [page, setPage] =useState('auth');



  const switchPage=(pageName)=>{
    setPage(pageName)
}
  return(

      <div className="App">
      <Header onLoadAuth={switchPage.bind(this, 'auth')} onLoadTodos={switchPage.bind(this,'todos')}/>
      <hr/>
      {page==='auth'?<Auth/>:<Todo/>}

      </div>

  )
}

export default app;
