//use means hook functions
import React, {useState} from 'react';

const todo = props =>{
    //useState passes two values, inputState[0] and inputState[1]
    const inputState = useState('');

    const inputChangeHandler=(event)=>{
        inputState[1](event.target.value)
    }
    //inputState[0] is a reference to latest state
    //inputState[1] is a function we can execute to update state to update 
    return (<React.Fragment>

        <input type='text' placeholder="Todo" onChange={inputChangeHandler} value={inputState[0]}/>
        <button type='button'>Add</button>
        <ul>

        </ul>
    </React.Fragment>)
};

export default todo;
